import React, { createContext, useContext, useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    const mapSupabaseUser = (sbUser) => {
        if (!sbUser) return null;
        const meta = sbUser.user_metadata || {};
        return {
            id: sbUser.id,
            email: sbUser.email,
            createdAt: sbUser.created_at,
            name: meta.name || '',
            phone: meta.phone || '',
            dob: meta.dob || '',
            gender: meta.gender || '',
            height: meta.height || '',
            weight: meta.weight || '',
            bloodType: meta.bloodType || '',
            allergies: meta.allergies || '',
            conditions: meta.conditions || '',
        };
    };

    useEffect(() => {
        // Initial session check
        supabase.auth.getSession().then(({ data: { session } }) => {
            setUser(mapSupabaseUser(session?.user));
            setLoading(false);
        });

        // Listen for auth changes
        const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
            setUser(mapSupabaseUser(session?.user));
        });

        return () => subscription.unsubscribe();
    }, []);

    const login = async (email, password) => {
        const { data, error } = await supabase.auth.signInWithPassword({
            email,
            password
        });
        if (error) return { success: false, error: error.message };
        return { success: true };
    };

    const signup = async ({ email, password, name, phone, dob, gender, height, weight, bloodType, allergies, conditions }) => {
        const { data, error } = await supabase.auth.signUp({
            email,
            password,
            options: {
                data: {
                    name, phone, dob, gender, height, weight, bloodType, allergies, conditions
                }
            }
        });
        if (error) return { success: false, error: error.message };
        return { success: true };
    };

    const logout = async () => {
        await supabase.auth.signOut();
    };

    return (
        <AuthContext.Provider value={{ user, login, signup, logout }}>
            {!loading && children}
        </AuthContext.Provider>
    );
};

// eslint-disable-next-line react-refresh/only-export-components
export const useAuth = () => useContext(AuthContext);
